mapper.select:
select
count(*)
from xs_content


		and id = #{id,jdbcType=VARCHAR}


		and book_id = #{bookId,jdbcType=VARCHAR}


		and book_name = #{bookName,jdbcType=VARCHAR}


		and zj_order = #{zjOrder,jdbcType=INTEGER}


		and zj_title = #{zjTitle,jdbcType=VARCHAR}


		and zj_url = #{zjUrl,jdbcType=VARCHAR}


		and content = #{content,jdbcType=LONGVARCHAR}


		and is_suc = #{isSuc,jdbcType=VARCHAR}


		and create_time = #{createTime,jdbcType=TIMESTAMP}

select

from xs_content


		and id = #{id,jdbcType=VARCHAR}


		and book_id = #{bookId,jdbcType=VARCHAR}


		and book_name = #{bookName,jdbcType=VARCHAR}


		and zj_order > #{zjOrder,jdbcType=INTEGER}


		and zj_title = #{zjTitle,jdbcType=VARCHAR}


		and zj_url = #{zjUrl,jdbcType=VARCHAR}


		and content = #{content,jdbcType=LONGVARCHAR}


		and is_suc = #{isSuc,jdbcType=VARCHAR}


		and create_time = #{createTime,jdbcType=TIMESTAMP}


order by zj_order

select

zj_url
from xs_content


		and id = #{id,jdbcType=VARCHAR}


		and book_id = #{bookId,jdbcType=VARCHAR}


		and book_name = #{bookName,jdbcType=VARCHAR}


		and zj_order = #{zjOrder,jdbcType=INTEGER}


		and zj_title = #{zjTitle,jdbcType=VARCHAR}


		and zj_url = #{zjUrl,jdbcType=VARCHAR}


		and is_suc = #{isSuc,jdbcType=VARCHAR}


		and create_time = #{createTime,jdbcType=TIMESTAMP}


order by zj_order

select

zj_url,
zj_order
from xs_content


		and id = #{id,jdbcType=VARCHAR}


		and book_id = #{bookId,jdbcType=VARCHAR}


		and book_name = #{bookName,jdbcType=VARCHAR}


		and zj_order = #{zjOrder,jdbcType=INTEGER}


		and zj_title = #{zjTitle,jdbcType=VARCHAR}


		and zj_url = #{zjUrl,jdbcType=VARCHAR}


		and is_suc = #{isSuc,jdbcType=VARCHAR}


		and create_time = #{createTime,jdbcType=TIMESTAMP}



order by zj_order desc
limit 1
